    // 每个画布的背景色
    var bgColor = ["#79AA8D","#52A6AF","#5E86AA","#97dac9","#9179A4","#C69B66"];

    // DOM 元素
    var $viewBox = $('.view-box');
    var polygon = $viewBox.find('polygon');
    var controlLis = $viewBox.find('li');
    var _index = 1;   // 用来标记当前的索引
    var timer = null;

    $('.control').on('click','li',function () {
      var index = $(this).index();
      clickEvent(index);
    });

    // 初始化
    function init() {
      controlLis.eq(0).click();
    }
    init();


    // 点击处理的事件
    function clickEvent(index) {
      clearTimeout(timer);
      var points = rePoints(index);
      var color = reColor(index);

      var oldPoints = rePoints(_index);

      upDataPolygon(points,color,oldPoints);

      _index = index; // 把当前的索引赋值给全局索引


      controlLis.eq(index).addClass('on').siblings().removeClass('on');

      $('.svg-animals').css('background-color',bgColor[index]);

      timer = setTimeout(function () {
        var i = 0;
        if(_index + 1 === controlLis.length){
          i = 0;
        }else{
          i = _index + 1;
        }
        controlLis.eq(i).click().addClass('on').siblings().removeClass('on');
      },5000);
    }


    // 更新每个Polygon
    function upDataPolygon(points,color,oldPoints) {
      $.each(polygon,function (index,item) {
        anime({
          targets: $(item).get(0),
          points: [
            oldPoints[index],
            points[index]
          ],
          easing: 'easeOutQuad',
          fill:color[index],
          duration: 1000,
          delay:40 * index,
          loop: false
        });
      });
    }

    function rePoints(num){
      return [pointsArray1,pointsArray2,pointsArray3,pointsArray4,pointsArray5,pointsArray6][num];
    }
    function reColor(num){
      return [polColor1,polColor2,polColor3,polColor4,polColor5,polColor6][num];
    }
  }
}
