<style lang="scss" scoped>
.svg-animals {
  display: flex;
  height:100%;
  transition: background-color 2s;
  overflow: hidden;
  background-color: #79AA8D;
}
.view-box {
  width: 960px;
  margin:auto;
}
.control {
  text-align: center;
  padding:30px 0;
  user-select: none;
  li {
      display: inline-block;
      width: 30px;
      height: 30px;
      cursor: pointer;
      position: relative;
      // 为了扩大鼠标触发区域
      &::before {
         display: block;
         content: '';
         position: absolute;
         top:50%;
         left:50%;
         transform: translate(-50%,-50%);
          width: 10px;height: 10px;
          border-radius: 50%;
          background-color: #fff;
      }
      &.on::before {
        background-color: #333;
      }
  }
}

@media screen and (max-width: 780px){
  .view-box {
    width: 100%;
  }
  svg {
    max-width: 100%;
    min-width: 100%;
  }
  .control {
    padding: 0;
  }
}
</style>
